    const lastJumpedAtString = context.item.data?.['last_jumped_at'];
    const intervalSeconds = context.item.data?.['interval_seconds'];

    if (!lastJumpedAtString || !intervalSeconds) {
        return "-";
    }

    // 2. 님의 '정상 작동 코드'와 "완전히 동일한 방식"으로 Date 객체를 생성합니다.
    //    어떠한 문자열 조작도 하지 않습니다.
    const lastJumpedDate = new Date(lastJumpedAtString);
    const now = new Date(); // 현재 시간도 동일한 방식으로 생성합니다.

    // 3. 다음 점프 시간을 계산합니다.
    const nextJumpTimestamp = lastJumpedDate.getTime() + (Number(intervalSeconds) * 1000);
    
    // 4. 남은 시간을 계산합니다.
    const remainingMilliseconds = nextJumpTimestamp - now.getTime();

    // 5. 결과 표시
    //    이제 이 로직이 정상적으로 작동할 것입니다.
    if (remainingMilliseconds <= 0) {
        // 이미 시간이 지났거나 즉시 실행되어야 할 때
        return "곧 실행";
    }
    
    if (remainingMilliseconds < 60 * 1000) {
        // 1분 미만
        return "곧 실행";
    }
    
    if (remainingMilliseconds < 3600 * 1000) {
        // 1시간 미만
        const remainingMinutes = Math.floor(remainingMilliseconds / (60 * 1000));
        return `${remainingMinutes}분 후`;
    }
    
    // 1시간 이상
    const remainingHours = Math.floor(remainingMilliseconds / (3600 * 1000));
    const minutesPart = Math.floor((remainingMilliseconds % (3600 * 1000)) / (60 * 1000));
    
    return minutesPart === 0 ? `${remainingHours}시간 후` : `${remainingHours}시간 ${minutesPart}분 후`;

} catch (e) {
    return "계산 오류";
}

