// Weweb의 context에서 인터벌 데이터를 가져옵니다.
const intervalSeconds = context.item.data?.['interval_seconds'];

// 데이터가 없거나 0이면 "-"로 표시합니다.
if (!intervalSeconds || intervalSeconds <= 0) {
    return "-";
}

// --- 단위 계산 ---
const SECONDS_IN_AN_HOUR = 3600; // 60 * 60

// 1. "시간" 단위를 계산합니다.
const hours = Math.floor(intervalSeconds / SECONDS_IN_AN_HOUR);

// 2. 남은 초를 계산하고, 그 초를 "분" 단위로 변환합니다.
const remainingSeconds = intervalSeconds % SECONDS_IN_AN_HOUR;
const minutes = Math.round(remainingSeconds / 60);

// --- 최종 문자열 조합 ---
const parts = [];

if (hours > 0) {
    parts.push(`${hours}시간`);
}

// 남은 시간이 1분 이상일 때만 분을 추가합니다.
if (minutes > 0) {
    parts.push(`${minutes}분`);
}

// 만약 계산 결과가 1분 미만이라 아무것도 없다면 (예: 30초)
// 최소 단위인 "1분"으로 표시해 줍니다.
if (parts.length === 0) {
    return "1분";
}

// 최종적으로 계산된 부분을 공백으로 합쳐서 반환합니다.
