// On Error 이벤트에서 제공하는 error 객체를 사용합니다.
const error = context.workflow.error

// 기본 에러 메시지
let userMessage = "알 수 없는 오류가 발생했습니다. 잠시 후 다시 시도해 주세요.";

// Supabase 에러는 'cause' 객체 안에 구체적인 정보가 담겨 있습니다.
if (error.cause && error.cause.code) {
  const code = error.cause.code;

  switch (code) {
    case 'user_already_exists':
    case 'email_exists': // 'user_already_exists'와 사실상 동일
      userMessage = "이미 가입된 이메일 주소입니다. 로그인을 시도해 주세요.";
      break;

    case 'weak_password':
      // Supabase는 비밀번호 정책에 대한 상세 정보를 함께 보낼 수 있습니다.
      // error.cause.reason을 확인하여 더 구체적인 안내를 할 수 있습니다.
      userMessage = "비밀번호가 너무 단순합니다. 더 강력한 비밀번호를 사용해 주세요.";
      break;

    case 'validation_failed':
      userMessage = "이메일 또는 비밀번호 형식이 올바르지 않습니다.";
      break;

    case 'over_email_send_rate_limit':
      userMessage = "인증 메일 요청 횟수를 초과했습니다. 잠시 후 다시 시도해 주세요.";
      break;

    case 'over_request_rate_limit':
      userMessage = "요청이 너무 많습니다. 잠시 후 다시 시도해 주세요.";
      break;

    case 'email_address_not_authorized':
      userMessage = "현재는 관리자가 허용한 이메일로만 가입할 수 있습니다. 다른 이메일을 사용하거나 관리자에게 문의하세요.";
      break;

    case 'signup_disabled':
    case 'email_provider_disabled':
      userMessage = "현재 신규 회원가입이 불가능합니다. 관리자에게 문의해 주세요.";
      break;

    default:
      // 위에 정의되지 않은 다른 Supabase 에러가 발생한 경우
      console.error("Supabase Auth Error:", error.cause); // 개발자 확인용 로그
      userMessage = `오류가 발생했습니다. 나중에 다시 시도해 주세요. (코드: ${code})`;
  }
} else if (error.message) {
  // cause 객체가 없는 일반적인 자바스크립트 에러의 경우
  userMessage = error.message;
}

// 최종적으로 처리된 사용자 메시지를 반환합니다.
return userMessage;

