    const currentTheme = variables['0c3d472b-034c-46c8-a10c-8f874093b17f'];

    // Toggle theme
    const newTheme = currentTheme === 'dark' ? 'light' : 'dark';

    // Update the theme variable
    variables['0c3d472b-034c-46c8-a10c-8f874093b17f'] = newTheme;

    // Apply theme to document
    const doc = wwLib.getFrontDocument();
    if (doc && doc.documentElement) {
      if (newTheme === 'dark') {
        doc.documentElement.classList.add('ww-dark-theme');
        doc.documentElement.classList.remove('ww-light-mode');
      } else {
        doc.documentElement.classList.remove('ww-dark-theme');
        doc.documentElement.classList.add('ww-light-mode');
      }
    }

    // Store theme preference in localStorage
    try {
      wwLib.getFrontWindow().localStorage.setItem('theme', newTheme);
    } catch (storageError) {
      console.error('Failed to save theme preference to localStorage:', storageError);
    }

    // Also update WeWeb's internal theme system
