/*
 * [역할] 사용자가 선택한 플랜과 보유량을 비교하여,
 * 가능한지 또는 얼마나 부족한지를 계산하고, '추천 주기'는 항상 반환합니다.
 */

// --- 1단계: 플랜별 필요 점프량 정의 ---
const PLAN_COST = {
    fast:   { cost: 1440, interval: 1,  name: "24시간 집중 공략" },
    steady: { cost: 1008, interval: 10, name: "7일 안정권 유지" },
    peak:   { cost: 240,  interval: 1,  name: "저녁 피크타임 집중" }
};

// --- 2단계: 사용자 입력값 가져오기 ---
const jumpsCount = context.component?.variables?.[/* Jumps Input - value */ '0360a173-94dd-4c2b-9a01-ded7ea0a48e4-value'] ?? 0;
const selectedPlanId = context.component?.variables?.[/* seleted_option */ '4de51457-6f74-48a3-8b44-571d67fc54c6'] ?? 'fast';


// --- 3단계: 계산 및 결과 생성 ---
let status = 'idle';
let displayText = "점프 수량을 입력하고 플랜을 선택해주세요.";
let recommendedInterval = null; // 기본값은 null

if (jumpsCount > 0) {
    // 먼저, 선택된 플랜 정보를 가져옵니다.
    const selectedPlan = PLAN_COST[selectedPlanId];
    
    // 중요: 추천 주기는 플랜이 정해지면 항상 고정된 값입니다.
    // 보유량과 상관없이 먼저 값을 할당합니다.
    recommendedInterval = selectedPlan.interval;

    // 이제 보유량을 비교하여 상태와 안내 문구만 결정합니다.
    if (jumpsCount >= selectedPlan.cost) {
        status = 'sufficient';
        displayText = `보유하신 ${jumpsCount}개로 '${selectedPlan.name}' 플랜을 실행하기에 충분합니다. 추천 주기(${recommendedInterval}분)로 설정하시겠습니까?`;
    } 
    else {
        const shortage = selectedPlan.cost - jumpsCount;
        status = 'insufficient';
        displayText = `'${selectedPlan.name}' 플랜을 실행하기에 점프가 ${shortage}개 부족합니다. 추가 구매 후 이용해주세요.`;
        // 더 이상 recommendedInterval을 null로 만들지 않습니다.
    }
}

// --- 4단계: 워크플로우 다음 단계를 위한 모든 정보 반환 ---
return {
    status: status,
    display_text: displayText,
    recommended_interval: recommendedInterval // 이제 부족할 때도 1, 10 같은 숫자 값을 가집니다.
};

